import React, { useState } from 'react';
import { Link, useNavigate, Outlet, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Layout = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [selectedNav, setSelectedNav] = useState('dashboard');
  const [isDropdownOpen, setDropdownOpen] = useState(false); // State to manage dropdown visibility

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleDropdown = () => {
    setSelectedNav('profile');
    setDropdownOpen(!isDropdownOpen);
  };

  const isAuthPage = location.pathname === '/login' || location.pathname === '/register';

  return (
    <div className='app-container'>
      {!isAuthPage && (
        <header className='navbar'>
          <div className='navbar-logo'>
            <Link to="/dashboard">BookShelf</Link>
          </div>
          <nav className='navbar-links' >
            {user ? (
              <>
                <Link to="/dashboard" onClick={setSelectedNav('dashboard')} className={'dashboard'=== selectedNav ? 'selected-nav' : 'nav-link'}>Dashboard</Link>
                <Link to="/mybooks" onClick={setSelectedNav('mybooks')}  className={'mybooks'=== selectedNav ? 'selected-nav' : 'nav-link'}>My Books</Link>
                <Link to="/friends" onClick={setSelectedNav('friends')}  className={'friends'=== selectedNav ? 'selected-nav' : 'nav-link'}>Friends</Link>
                {/* Profile dropdown */}
                <div className="profile-dropdown">
                  <button onClick={toggleDropdown}  className={'profile'=== selectedNav ? 'selected-nav' : 'nav-link'}>
                    Profile
                  </button>
                  {isDropdownOpen && (
                    <div className="dropdown-menu">
                      <Link to="/profile" className="dropdown-item">My Profile</Link>
                      <button onClick={handleLogout} className="dropdown-item">Logout</button>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <>
                <Link to="/login" className='nav-link'>Login</Link>
                <Link to="/register" className='nav-link'>Register</Link>
              </>
            )}
          </nav>
        </header>
      )}
      <main className='main-content'>
        <Outlet />
      </main>
    </div>
  );
};

export default Layout;
