import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { sendMessage, fetchMessages } from '../services/api'; // API calls for sending and fetching messages
// import io from 'socket.io-client'; // Import Socket.IO client

// const socket = io('http://localhost:5000'); // Connect to Socket.IO server

const Chat = ({ friend }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const sender = useAuth().user.payload.user.username;
  const senderId = useAuth().user.payload.user.id;

  useEffect(() => {
    const loadMessages = async () => {
      try {
        const fetchedMessages = await fetchMessages(senderId, friend._id);
        setMessages(fetchedMessages);
      } catch (error) {
        console.error('Error fetching messages:', error);
      }
    };

    loadMessages();

    // // Listen for incoming messages
    // socket.on('receive_message', (message) => {
    //   setMessages((prevMessages) => [...prevMessages, message]);
    // });

    // // Clean up the listener when the component unmounts
    // return () => {
    //   socket.off('receive_message');
    // };
  }, [friend._id, senderId]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    try {
      const messageData = { senderId: senderId, receiverId: friend._id, content: newMessage };
      const sentMessage = await sendMessage(messageData);
      setMessages((prevMessages) => [...prevMessages, sentMessage]);
      setNewMessage('');

      // Emit the message to the server
      // socket.emit('send_message', messageData);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="chat-container">
      <h2>Chat with {friend.username}</h2>
      <div className="messages">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message ${message.senderId === senderId ? 'sent' : 'received'}`}
          >
            <strong>
              {message.senderId === senderId ? sender : friend.username}:
            </strong>
            <span className="message-content">{message.content}</span>
          </div>
        ))}
      </div>
      <form onSubmit={handleSendMessage} className="send-message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type your message..."
          required
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Chat;
