import React, { useState, useEffect } from 'react';
import { Link, useNavigate, Outlet, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Layout = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [selectedNav, setSelectedNav] = useState('dashboard');
  const [isDropdownOpen, setDropdownOpen] = useState(false);

  useEffect(() => {
    // Update selectedNav based on current path
    const route = location.pathname.split('/'); // Get the first part of the path
    const path = [];
    console.log("path------", location, route, path);
    if(route.length > 2){
      path = route[length - 1];
    } else {
      path = route[1];
    }

    if (path === 'dashboard' || path === 'mybooks' || path === 'friends/messages' || path === 'friends' || path === 'profile') {
      setSelectedNav(path);
    } else if (path === 'friends' && location.pathname.includes('/messages')) {
      setSelectedNav('chat');
    }
  }, [location]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleDropdown = () => {
    setSelectedNav('profile');
    setDropdownOpen(!isDropdownOpen);
  };

  const handleNavClick = (type) => {
    setSelectedNav(type);
  };

  const isAuthPage = location.pathname === '/login' || location.pathname === '/register';

  return (
    <div className='app-container'>
      {!isAuthPage && (
        <header className='navbar'>
          <div className='navbar-logo'>
            <Link to="/dashboard">BookShelf</Link>
          </div>
          <nav className='navbar-links'>
            {user ? (
              <>
                <Link
                  to="/dashboard"
                  onClick={() => handleNavClick('dashboard')}
                  className={selectedNav === 'dashboard' ? 'selected-nav' : 'nav-link'}
                >
                  Dashboard
                </Link>
                <Link
                  to="/mybooks"
                  onClick={() => handleNavClick('mybooks')}
                  className={selectedNav === 'mybooks' ? 'selected-nav' : 'nav-link'}
                >
                  My Books
                </Link>
                <Link
                  to="/friends"
                  onClick={() => handleNavClick('friends')}
                  className={selectedNav === 'friends' ? 'selected-nav' : 'nav-link'}
                >
                  Friends
                </Link>
                <Link
                  to="/friends/messages"  // Add a new link for the Chat page
                  onClick={() => handleNavClick('chat')}
                  className={selectedNav === 'chat' ? 'selected-nav' : 'nav-link'}
                >
                  Chat
                </Link>
                <div className="profile-dropdown">
                  <button
                    onClick={toggleDropdown}
                    className={selectedNav === 'profile' ? 'selected-nav' : 'nav-link'}
                  >
                    Profile
                  </button>
                  {isDropdownOpen && (
                    <div className="dropdown-menu">
                      <Link to="/profile" className="dropdown-item">My Profile</Link>
                      <button onClick={handleLogout} className="dropdown-item">Logout</button>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <>
                <Link to="/login" className='nav-link'>Login</Link>
                <Link to="/register" className='nav-link'>Register</Link>
              </>
            )}
          </nav>
        </header>
      )}
      <main className='main-content'>
        <Outlet />
      </main>
    </div>
  );
};

export default Layout;
