import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { fetchFriends, searchUsers, sendFriendRequest, fetchFriendRequests, respondToFriendRequest  } from '../services/api'; // Import the API calls
import Notification from './Notification';
import FriendProfile from './FriendProfile'; // Import the FriendProfile component
import Chat from './Chat'; // Import the Chat component

const Friends = () => {
  const [friends, setFriends] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedFriend, setSelectedFriend] = useState();
  const [selectedFriendId, setSelectedFriendId] = useState(null); // State to track the selected friend
  const [isChatOpen, setIsChatOpen] = useState(false); // State to track if a chat is open
  const [requests, setRequests] = useState([]);
  const senderId = useAuth().user.payload.user.id;
  const [showNotification, setShowNotification] = useState(false);

  useEffect(() => {
    const fetchFriendsData = async () => {
      try {
        const response = await fetchFriends(senderId);
        setFriends(response);
      } catch (error) {
        console.error('Error fetching friends:', error);
      }
    };

    const fetchRequests = async () => {
      try {
        const response = await fetchFriendRequests();
        setRequests(response.receivedRequests);
      } catch (error) {
        console.error('Error fetching friend requests:', error);
      }
    };

    fetchRequests();

    fetchFriendsData();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await searchUsers(searchTerm);
      setSearchResults(response);
      console.log("searchFriend------", searchResults);

    } catch (error) {
      console.error('Error searching users:', error);
    }
  };

  const handleSendRequest = async (receiverId) => {
    try {
      await sendFriendRequest(receiverId, senderId);
      setShowNotification(true);
      setTimeout(() => setShowNotification(false), 3000);
      setSearchTerm('')
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handleResponse = async (requestId, action) => {
    try {
      await respondToFriendRequest(senderId, requestId, action);
      setRequests(requests.filter(request => request._id !== requestId));
    } catch (error) {
      console.error(`Error ${action === 'accept' ? 'accepting' : 'declining'} friend request:`, error);
    }
  };

  const handleFriendClick = (friendId) => {
    setSelectedFriendId(friendId);
    setIsChatOpen(false); // Close chat if open
  };

  const handleChatClick = (friend) => {
    setSelectedFriend(friend);
    setIsChatOpen(true); // Open chat
  };

  return (
    <div className="friends-container">
      <div className="friends-list">
        <h1>Your Friends</h1>
        <div>
          <h3>Friend Requests</h3>
          <ul>
            {requests.map(request => (
              <li key={request._id}>
                {request.sender.username} ({request.sender.email})
                <button onClick={() => handleResponse(request._id, 'accept')}>Accept</button>
                <button onClick={() => handleResponse(request._id, 'decline')}>Decline</button>
              </li>
            ))}
          </ul>
        </div>
        <form onSubmit={handleSearch} className='search-container'>
          <input
            type="text"
            placeholder="Search for users..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>

        {searchResults && searchResults.length > 0 ? (
          <>
            <h2>Search Results</h2>
            <ul>
              {searchResults.map(user => (
                <li key={user._id}>
                  {user.username}
                  <button onClick={() => handleSendRequest(user._id)}>Add Friend</button>
                  {showNotification && (
                    <Notification
                      message="Friend Request sent!"
                      onClose={() => setShowNotification(false)}
                    />
                  )}
                </li>
              ))}
            </ul>
          </>
        ) : (
          <></>
          // <p>No results found.</p>
        )}


        <h2>Friends List</h2>
        <ul>
          {friends.map(friend => (
            <li
              key={friend._id}
              onClick={() => handleFriendClick(friend._id)}
              style={{ cursor: 'pointer' }}
              className={friend._id === selectedFriendId && !isChatOpen ? 'selected-friend' : ''}
            >
              {friend.username}
            </li>
          ))}
        </ul>

        <h2>Chats</h2>
        <ul>
          {friends.map(friend => (
            <li
              key={friend._id}
              onClick={() => handleChatClick(friend)}
              style={{ cursor: 'pointer' }}
              className={friend._id === selectedFriendId && isChatOpen ? 'selected-friend' : ''}
            >
              {friend.username}
            </li>
          ))}
        </ul>
      </div>

      <div className="friend-profile-or-chat">
        {selectedFriendId ? (
          isChatOpen ? (
            <Chat friend={selectedFriend} />
          ) : (
            <FriendProfile friendId={selectedFriendId} />
          )
        ) : (
          <p>Select a friend to view their profile or start a chat</p>
        )}
      </div>
    </div>
  );
};

export default Friends;
