import React, { useState, useEffect } from 'react';
import { getBookWithStatus, addReview, editReview, deleteReview } from '../services/api';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const BookDetail = () => {
  const [book, setBook] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [isAddingReview, setIsAddingReview] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [reviewText, setReviewText] = useState('');
  const [reviewToEdit, setReviewToEdit] = useState('');
  const { bookId } = useParams();
  const { user } = useAuth();
  const userId = user.payload.user.id;

  useEffect(() => {
    fetchBook();
    // fetchReviews();
  }, [bookId]);

  const fetchBook = async () => {
    try {
        console.log("getBookWithStatus-------", userId,bookId );

      const fetchedBook = await getBookWithStatus(bookId, userId);
      setBook(fetchedBook);
      setReviews(fetchedBook.review);
      console.log(fetchedBook);

    } catch (error) {
      console.error('Error fetching book:', error);
    }
  };

//   const fetchReviews = async () => {
//     try {
//       const fetchedReviews = await getReviews(userId, bookId);
//     //   setReviews(fetchedReviews);
//     } catch (error) {
//       console.error('Error fetching reviews:', error);
//     }
//   };

  const handleReviewChange = (e) => {
    setReviewText(e.target.value);
    console.log("handleReviewChange---------", reviewText);

  };

  const handleAddReview = async () => {
    try {
      await addReview(userId, bookId, reviewText);
      setReviewText('');
      setIsAddingReview(false);
      fetchBook();
    } catch (error) {
      console.error('Error adding review:', error);
    }
  };

  const handleEditReview = async (reviewId) => {
    try {
      await editReview(userId, reviewId, reviewText);
      setIsEditing(false);
      setReviewText('');
    //   fetchReviews();
    } catch (error) {
      console.error('Error editing review:', error);
    }
  };

  const handleDeleteReview = async (reviewId) => {
    try {
      await deleteReview(reviewId);
    //   fetchReviews();
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  };

  if (!book) return <div>Loading...</div>;

  return (
    <div>
      <h2>{book.title}</h2>
      <p>Author: {book.author}</p>
      <p>Description: {book.description}</p>
      <p>Rating: {book.rating}</p>
      <p>Status: {book.status}</p>

      {reviews ? (
        // reviews.map((rev) => (
          <div key={reviews._id} className="review-container">
            {isEditing && reviews === reviewText ? (
              <div>
                <input
                  type='text'
                  placeholder="Edit your review"
                  value={reviewText}
                  onChange={handleReviewChange}
                />
                <button onClick={() => handleEditReview(reviews)}>Save Review</button>
              </div>
            ) : (
              <div>
                <h3>Your Review:</h3>
                <p>{reviews}</p>
                <button onClick={() => {
                  setIsEditing(true);
                  setReviewText(reviews)
                }}>Edit Review</button>
                <button onClick={() => handleDeleteReview(reviews)}>Delete Review</button>
              </div>
            )}
          </div>
        // ))
      ) : (
        <div>
          {isAddingReview ? (
            <div>
              <input
                type='text'
                placeholder="Add your review"
                value={reviewText}
                onChange={handleReviewChange}
              />
              <button onClick={handleAddReview}>Add Review</button>
            </div>
          ) : (
            <button onClick={() => setIsAddingReview(true)}>Add Review</button>
          )}
        </div>
      )}

      <Link to="/books">
        <button>Back to List</button>
      </Link>
    </div>
  );
};

export default BookDetail;
