import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      console.log("token-------", localStorage);

      if (token) {
        try {
          // Verify the token with your backend
          const response = await axios.get('/api/auth/verify', {
            headers: { 'x-auth-token': token },
          });
          setUser(response.data.user); // Set the user data from the response
        } catch (error) {
          console.error('Error verifying token:', error);
          localStorage.removeItem('token'); // Remove the invalid token
        }
      }
      setLoading(false); // Loading complete
    };

    initializeAuth();
  }, []);

  // const login = async (email, password) => {
  //   try {
  //     const response = await axios.post('/api/auth/login', { email, password });
  //     localStorage.setItem('token', response.data.token);
  //     setUser(response.data.user); // Assuming the user data is returned on login
  //   } catch (error) {
  //     console.error('Login error:', error);
  //     throw error;
  //   }
  // };

  // const register = async (username, email, password) => {
  //   try {
  //     const response = await axios.post('/api/auth/register', { username, email, password });
  //     localStorage.setItem('token', response.data.token);
  //     setUser(response.data.user); // Assuming the user data is returned on registration
  //   } catch (error) {
  //     console.error('Registration error:', error);
  //     throw error;
  //   }
  // };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
  };

  if (loading) {
    return <div>Loading...</div>; // Replace with a more sophisticated loading indicator if needed
  }

  return (
    <AuthContext.Provider value={{ user, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
