import React, { useState } from 'react';
import { Link, useNavigate, Outlet, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import FriendRequests from './FriendRequests';

const Layout = () => {
  const { user, logout } = useAuth();
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const closeDropdown = () => {
    setDropdownOpen(false);
  };


  const isAuthPage = location.pathname === '/login' || location.pathname === '/register';

  return (
    <div className='app-container'>
      {!isAuthPage && (
        <header className='navbar'>
          <div className='navbar-logo'>
            <Link to="/dashboard">BookShelf</Link>
          </div>
          <nav className='navbar-links'>
            {user ? (
              <>
                {/* <Link to="/" className='nav-link'>Home</Link> */}
                <Link to="/dashboard" className='nav-link' onClick={closeDropdown}>Dashboard</Link>
                <Link to="/mybooks" className='nav-link' onClick={closeDropdown}>My Books</Link>
                <Link to="/friends">Friends</Link>
                {/* <Link to="/add-book" className='nav-link'>Add Book</Link>
                <button className='nav-link logout-btn' onClick={handleLogout}>Logout</button> */}
                <div className="profile-dropdown nav-link" onClick={toggleDropdown}>
                  &#9776;
                  {dropdownOpen && (
                    <div className="dropdown-menu">
                      <Link to="/profile" className="dropdown-item" onClick={toggleDropdown}>My Profile</Link>
                      <button onClick={handleLogout} className="dropdown-item logout-btn">Logout</button>
                    </div>
                  )}
                </div>
              </>
            ) : (
              <>
                <Link to="/login" className='nav-link'>Login</Link>
                <Link to="/register" className='nav-link'>Register</Link>
              </>
            )}
          </nav>
        </header>
      )}
      <aside>
        <FriendRequests />
      </aside>
      <main className='main-content'>
        <Outlet />
      </main>
    </div>
  );
};

export default Layout;
